const initState = {
    trangthai: false,
    ngaynhanphong:'',
}

const datPhongReducer = (state = initState, action) => {
    switch (action.type) {
        case "CONFIRM_BOOK":
        // Firebaseone.child(action.uniqueKey).update({
        //     cmnd : action.cmnd,
        //     diachi : action.address,
        //     ngaynhanphong : action.ngaynhanphong,
        //     sdt : action.sdt,
        //     tenkhach : action.tenkhach,
        //     trangthai : 0,
        // })

        return{...state,data: {action,success:'Dat phong thanh cong'}}
        default:
            return state
    }
}

export default datPhongReducer
