import {Firebaseone} from '../Firebaseconntion';

const initState = {
    trangthai: false,
    ngaynhanphong:'',
}

const datPhongReducer = (state = initState, action) => {
    switch (action.type) {
        case "CONFIRM_BOOK":
        Firebaseone.child(action.uniqueKey).update({
            cmnd : action.cmnd,
            diachi : action.address,
            gioitinh : action.gioitinh,
            ngaydatphong : action.ngaydat,
            ngaysinh : action.ngaysinh,
            sdt : action.sdt,
            tenkhach : action.tenkhach,
            trangthai : action.trangthai,
        })
        console.log(action.data);
        return{...state}
        default:
            return state
    }
}

export default datPhongReducer
