import React from 'react'
import './../../styles.css'
import { Link, Redirect } from 'react-router-dom'
import { base } from '../../Firebaseconntion';
import { Toaster, Intent } from '@blueprintjs/core'

const providers = {
    googleProvider: new base.auth.GoogleAuthProvider(),
}

class Login extends React.Component {
    constructor() {
        super();
        this.state = {
            redirect : false
        }
    }

    handleLogin = async event => {
        event.preventDefault();
        const { email, password } = event.target.elements;

        try {
            await base.auth().setPersistence(base.auth.Auth.Persistence.SESSION)
            .then(()=>{
                return base.auth().signInWithEmailAndPassword(email.value,password.value);
            });
            this.setState({ redirect : true })
        }
        catch (err) {
            this.toaster.show({ intent: Intent.DANGER, message: err.message })
            console.log(err)
        }
    }

    googleLogin =async () => {
        try {
            await base.auth().signInWithPopup(providers.googleProvider)
            this.setState({ redirect : true })
        }
        catch (err) {
            this.toaster.show({ intent: Intent.DANGER, message: 'Unable to sign in with google.' })
        }
    }

    facebookLogin = async ()=>{
        try {
            await base.auth().signInWithPopup(providers.googleProvider)
            this.setState({ redirect : true })
        }
        catch (err) {
            this.toaster.show({ intent: Intent.DANGER, message: 'Unable to sign in with google.' })
        }
    }
    render() {
        if(this.state.redirect === true){
            return <Redirect to='/quanly-nhanvien' />
        }
        return (
            <div className="container" style={{ marginTop: '100px !important' }}>
                <div className="d-flex justify-content-center h-100">
                    <div className="card">
                        <div className="card-header">
                            {/* <Toaster ref={(element) => this.toaster = element} /> */}
                            <h3>Sign In</h3>
                            <div className="d-flex justify-content-end social_icon">
                                <button onClick={() => this.facebookLogin()} style={{ margin: "0", padding: '0' }}><span><i className="fab fa-facebook-square" /></span></button>
                                <button onClick={() => this.googleLogin()} style={{ margin: "0", padding: '0' }}><span><i className="fab fa-google-plus-square" /></span></button>
                                <button onClick={() => this.googleLogin()} style={{ margin: "0", padding: '0' }}><span><i className="fab fa-twitter-square" /></span></button>
                                {/* <span><i className="fab fa-facebook-square" /></span>
                                        <span><i className="fab fa-google-plus-square" /></span>
                                        <span><i className="fab fa-twitter-square" /></span> */}
                            </div>
                        </div>

                        <div className="card-body">
                            <Toaster ref={(element) => {this.toaster = element}} />
                            <form action="#" onSubmit={(event)=>this.handleLogin(event)}>
                                <div className="input-group form-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"><i className="fas fa-user" /></span>
                                    </div>
                                    <input type="email" className="form-control" name='email' defaultValue='laquyettam1@gmail.com' placeholder="Email" />
                                </div>
                                <div className="input-group form-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text"><i className="fas fa-key" /></span>
                                    </div>
                                    <input type="password" className="form-control" name='password' placeholder="Passwprd" />
                                </div>
                                <div className="row align-items-center remember">
                                    <input type="checkbox" name='remember' />Remember Me
                                        </div>
                                <div className="form-group">
                                    <input type="submit" defaultValue="Login" className="btn float-right login_btn" />
                                </div>
                            </form>
                        </div>
                        <div className="card-footer">
                            <div className="d-flex justify-content-center links">
                                Don't have an account?<Link to="/sign-up">Sign Up</Link>
                            </div>
                            <div className="d-flex justify-content-center">
                                <a href="#">Forgot your password?</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }


}
export default (Login);
